import React, { useState, useEffect } from 'react';
import { FaSearch, FaUsers } from 'react-icons/fa';
import './HR.css';

const HR = ({ onViewProfile }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [entriesPerPage, setEntriesPerPage] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch employees from API
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        setLoading(true);
        setError('');
        
        // Get token from localStorage (where login saves it)
        const token = localStorage.getItem('token');
        
        if (!token) {
          throw new Error('No authentication token found. Please login again.');
        }

        // API endpoint - NOTE: Changed port to 3000 (your backend port)
        const response = await fetch('http://localhost:3000/api/hr/employees', { 
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` // Add authorization header
          }
        });

        if (!response.ok) {
          if (response.status === 401) {
            throw new Error('Authentication failed. Please login again.');
          }
          throw new Error(`Failed to fetch employees: ${response.status}`);
        }

        const data = await response.json();
        console.log('API Response:', data); // Debug log
        
        // Handle API response structure
        if (data.operation === 'success' && Array.isArray(data.data)) {
          setEmployees(data.data);
        } else {
          throw new Error('Unexpected API response format');
        }
      } catch (err) {
        console.error('Error fetching employees:', err);
        setError(err.message);
        setEmployees([]);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, []);

  // Map API field names to your component field names
  const mappedEmployees = employees.map(employee => ({
    empId: employee.EmpID,
    status: employee.Status,
    name: employee.Name,
    reportTo: employee.ReportTo,
    division: employee.Division,
    department: employee.Department,
    designation: employee.Designation,
    grade: employee.Grade,
    zone: employee.Zone,
    branch: employee.Branch,
    mobile: employee.Mobile,
    email: employee['E-Mail'] // Note: API returns 'E-Mail' with hyphen
  }));

  const filteredEmployees = mappedEmployees.filter(employee => 
    employee.name && employee.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Calculate active users
  const activeUsers = mappedEmployees.filter(emp => emp.status === 'Active').length;

  const totalEntries = filteredEmployees.length;
  const entriesToShow = entriesPerPage ? parseInt(entriesPerPage) : totalEntries;
  const totalPages = Math.ceil(totalEntries / entriesToShow);
  
  const startIndex = (currentPage - 1) * entriesToShow;
  const endIndex = startIndex + entriesToShow;
  const currentEmployees = filteredEmployees.slice(startIndex, endIndex);

  const handleSearchClick = (employee) => {
    onViewProfile(employee);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  if (loading) {
    return (
      <div className="hr-container">
        <div className="loading-message">Loading employees...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="hr-container">
        <div className="error-message">
          Error: {error}
          <button 
            onClick={() => window.location.reload()} 
            className="retry-btn"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="hr-container">
      <div className="hr-header">
        <div className="header-main">
          <h1 className="main-title">Employee Search</h1>
          <div className="total-count">Total Employees: {mappedEmployees.length}</div>
        </div>
        <div className="user-stats">
          <div className="active-users">
            <div className="active-header">
              <FaUsers className="users-icon" />
              <span className="active-label">Active Users</span>
            </div>
            <span className="active-count">{activeUsers}</span>
          </div>
        </div>
      </div>

      <div className="search-filters">
        <div className="search-box">
          <input
            type="text"
            placeholder="Search employees by name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
        <div className="filter-box">
          <select
            value={entriesPerPage}
            onChange={(e) => {
              setEntriesPerPage(e.target.value);
              setCurrentPage(1);
            }}
            className="entries-filter"
          >
            <option value="">Show all</option>
            <option value="5">5 entries</option>
            <option value="10">10 entries</option>
            <option value="20">20 entries</option>
            <option value="50">50 entries</option>
          </select>
        </div>
      </div>

      <div className="table-container">
        <table className="employees-table">
          <thead>
            <tr>
              <th>EmpID</th>
              <th>Status</th>
              <th>Name</th>
              <th>Report To</th>
              <th>Division</th>
              <th>Department</th>
              <th>Designation</th>
              <th>Grade</th>
              <th>Zone</th>
              <th>Branch</th>
              <th>Mobile</th>
              <th>Email</th>
              <th>Display</th>
            </tr>
          </thead>
          <tbody>
            {currentEmployees.length > 0 ? (
              currentEmployees.map((employee, index) => (
                <tr key={employee.empId || index}>
                  <td className="text-black">{employee.empId || 'N/A'}</td>
                  <td>
                    <span className={`status-badge ${employee.status ? employee.status.toLowerCase() : 'unknown'}`}>
                      {employee.status || 'Unknown'}
                    </span>
                  </td>
                  <td className="text-black">{employee.name || 'N/A'}</td>
                  <td className="text-black">{employee.reportTo || 'N/A'}</td>
                  <td className="text-black">{employee.division || 'N/A'}</td>
                  <td className="text-black">{employee.department || 'N/A'}</td>
                  <td className="text-black">{employee.designation || 'N/A'}</td>
                  <td className="text-black">{employee.grade || 'N/A'}</td>
                  <td className="text-black">{employee.zone || 'N/A'}</td>
                  <td className="text-black">{employee.branch || 'N/A'}</td>
                  <td className="text-black">{employee.mobile || 'N/A'}</td>
                  <td className="text-black">{employee.email || 'N/A'}</td>
                  <td className="display-cell">
                    <button 
                      className="search-icon-btn"
                      onClick={() => handleSearchClick(employee)}
                      title="View Profile"
                    >
                      <FaSearch />
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr className="no-data-row">
                <td colSpan="13" className="text-black">
                  {mappedEmployees.length === 0 ? 'No employees found' : 'No employees match your search'}
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {totalPages > 1 && (
          <div className="pagination-controls">
            <button
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
              className="pagination-btn"
            >
              Previous
            </button>
            <span className="pagination-info">Page {currentPage} of {totalPages}</span>
            <button
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
              className="pagination-btn"
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default HR;
