import React, { useState, useEffect } from 'react';
import { FaArrowLeft } from 'react-icons/fa';
import './EmployeeProfile.css';

const EmployeeProfile = ({ employee, onBack }) => {
  const [activeTab, setActiveTab] = useState('personal');
  const [detailedEmployee, setDetailedEmployee] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDetailedProfile = async () => {
      if (!employee || !employee.empId) return;
      
      try {
        setLoading(true);
        const token = localStorage.getItem('token');
        
        if (!token) {
          throw new Error('No authentication token found');
        }

        const response = await fetch(`http://localhost:5000/api/hr/profile-detailed/${employee.empId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }

        const data = await response.json();
        
        if (data.operation === 'success') {
          setDetailedEmployee(data.data);
        } else {
          throw new Error(data.message || 'Failed to load employee details');
        }
      } catch (err) {
        console.error('Error fetching detailed profile:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDetailedProfile();
  }, [employee]);

  if (!employee) {
    return (
      <div className="profile-container">
        <div className="profile-error">
          <h2>Employee Not Found</h2>
          <button onClick={onBack} className="back-btn">
            <FaArrowLeft className="back-icon" />
            Back to Employee Search
          </button>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading-message">Loading employee details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container">
        <div className="profile-error">
          <h2>Error Loading Profile</h2>
          <p>{error}</p>
          <button onClick={onBack} className="back-btn">
            <FaArrowLeft className="back-icon" />
            Back to Employee Search
          </button>
        </div>
      </div>
    );
  }

  // Use detailedEmployee data when available, fallback to basic employee data
  const empData = detailedEmployee || employee;

  const renderPersonalInfo = () => (
    <div className="tab-content">
      <div className="info-grid">
        <div className="info-card">
          <h4>Basic Information</h4>
          <div className="detail-row">
            <span className="label">Employee ID</span>
            <span className="value">{empData.EmpID || employee.empId}</span>
          </div>
          <div className="detail-row">
            <span className="label">Full Name</span>
            <span className="value">{empData.FirstName || employee.name}</span>
          </div>
          <div className="detail-row">
            <span className="label">Father Name</span>
            <span className="value">{empData.FatherName || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">Date of Birth</span>
            <span className="value">{empData.DOB || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">Gender</span>
            <span className="value">{empData.GenderID || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">Blood Group</span>
            <span className="value">{empData.BloodGroupID || 'N/A'}</span>
          </div>
        </div>

        <div className="info-card">
          <h4>Contact Information</h4>
          <div className="detail-row">
            <span className="label">Office Email</span>
            <span className="value">{empData.OfficeEmail || employee.email}</span>
          </div>
          <div className="detail-row">
            <span className="label">Personal Email</span>
            <span className="value">{empData.PersonalEmail || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">Office Mobile</span>
            <span className="value">{empData.OfficeMobile || employee.mobile}</span>
          </div>
          <div className="detail-row">
            <span className="label">Home Phone</span>
            <span className="value">{empData.HomePhone || 'N/A'}</span>
          </div>
        </div>

        <div className="info-card">
          <h4>Address</h4>
          <div className="detail-row">
            <span className="label">Street</span>
            <span className="value">{empData.HAStreet || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">Street No</span>
            <span className="value">{empData.HAStreetNo || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">Block</span>
            <span className="value">{empData.HABlock || 'N/A'}</span>
          </div>
          <div className="detail-row">
            <span className="label">City</span>
            <span className="value">{empData.HACity || 'N/A'}</span>
          </div>
        </div>
      </div>
    </div>
  );


  return (
    <div className="profile-container">
      <div className="profile-content">
        <div className="profile-card">
          {/* Header Section */}
          <div className="profile-header-info">
            <div className="profile-image-container">
              {empData.ImgPath ? (
                <img 
                  src={empData.ImgPath} 
                  alt={`${empData.FirstName || employee.name}`}
                  className="profile-picture"
                />
              ) : (
                <div className="profile-avatar">
                  {(empData.FirstName || employee.name) ? (empData.FirstName || employee.name).charAt(0).toUpperCase() : '?'}
                </div>
              )}
            </div>
            <div className="profile-basic-info">
              <div className="employee-header">
                <h2>{empData.FirstName || employee.name}</h2>
                <span className={`status-badge ${empData.Status ? empData.Status.toLowerCase() : employee.status?.toLowerCase()}`}>
                  {empData.Status || employee.status}
                </span>
              </div>
              <p className="employee-designation">{empData.DesignationName || employee.designation}</p>
              <p className="employee-department">{empData.DepartmentName || employee.department} â€¢ {empData.PayrollName || employee.division}</p>
              <p className="employee-since">SERVED [{empData.SinceDate || 'Year - Month - Days: '}]</p>
            </div>
          </div>

          {/* Tabs Navigation */}
          <div className="profile-tabs">
            <button 
              className={`tab-button ${activeTab === 'personal' ? 'active' : ''}`}
              onClick={() => setActiveTab('personal')}
            >
              Personal Information
            </button>
            <button 
              className={`tab-button ${activeTab === 'employment' ? 'active' : ''}`}
              onClick={() => setActiveTab('employment')}
            >
              Employment Details
            </button>
            <button 
              className={`tab-button ${activeTab === 'financial' ? 'active' : ''}`}
              onClick={() => setActiveTab('financial')}
            >
              Financial Information
            </button>
            <button 
              className={`tab-button ${activeTab === 'documents' ? 'active' : ''}`}
              onClick={() => setActiveTab('documents')}
            >
              Documents & IDs
            </button>
          </div>

          {/* Tab Content */}
          <div className="tab-container">
            {activeTab === 'personal' && renderPersonalInfo()}
            {activeTab === 'employment' && renderEmploymentInfo()}
            {activeTab === 'financial' && renderFinancialInfo()}
            {activeTab === 'documents' && renderDocumentsInfo()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeProfile;
