import React, { useState, useEffect } from 'react';
import { FaSearch, FaUsers, FaSort, FaSortUp, FaSortDown } from 'react-icons/fa';
import './HR.css';

const EmployeeList = ({ onViewProfile }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [entriesPerPage, setEntriesPerPage] = useState(10); // default 10 entries
  const [currentPage, setCurrentPage] = useState(1);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: 'empId', direction: 'desc' }); // default sorting

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        setLoading(true);
        setError('');

        const token = localStorage.getItem('token');
        if (!token) throw new Error('No authentication token found. Please login again.');

        const response = await fetch('http://localhost:5000/api/hr/employees', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          if (response.status === 401) throw new Error('Authentication failed. Please login again.');
          throw new Error(`Failed to fetch employees: ${response.status}`);
        }

        const data = await response.json();
        if (data.operation === 'success' && Array.isArray(data.data)) setEmployees(data.data);
        else throw new Error('Unexpected API response format');
      } catch (err) {
        console.error(err);
        setError(err.message);
        setEmployees([]);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, []);

  const mappedEmployees = employees.map(emp => ({
    empId: emp.EmpID,
    status: emp.Status,
    name: emp.Name,
    reportTo: emp.ReportTo,
    division: emp.Division,
    department: emp.Department,
    designation: emp.Designation,
    grade: emp.Grade,
    zone: emp.Zone,
    branch: emp.Branch,
    mobile: emp.Mobile,
    email: emp['E-Mail']
  }));

  // Filter employees by search term
  const filteredEmployees = mappedEmployees.filter(emp =>
    emp.name?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sorting function
  const sortedEmployees = [...filteredEmployees].sort((a, b) => {
    if (!sortConfig.key) return 0;

    const valueA = a[sortConfig.key];
    const valueB = b[sortConfig.key];

    if (valueA < valueB) return sortConfig.direction === 'asc' ? -1 : 1;
    if (valueA > valueB) return sortConfig.direction === 'asc' ? 1 : -1;
    return 0;
  });

  const totalEntries = sortedEmployees.length;
  const totalPages = Math.ceil(totalEntries / entriesPerPage);
  const startIndex = (currentPage - 1) * entriesPerPage;
  const endIndex = startIndex + entriesPerPage;
  const currentEmployees = sortedEmployees.slice(startIndex, endIndex);

  const handleSearchClick = employee => onViewProfile(employee);
  const handlePageChange = page => setCurrentPage(page);

  const requestSort = key => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') direction = 'desc';
    setSortConfig({ key, direction });
  };

  const getSortIcon = key => {
    if (sortConfig.key !== key) return <FaSort />;
    return sortConfig.direction === 'asc' ? <FaSortUp /> : <FaSortDown />;
  };

  if (loading) return <div className="loading-container"><p>Loading employees...</p></div>;
  if (error) return (
    <div className="error-container">
      <p>Error: {error}</p>
      <button onClick={() => window.location.reload()} className="retry-button">Retry</button>
    </div>
  );

  return (
    <div className="employee-list-container">
      <div className="list-header">
        <div className="list-stats">
          <div className="stat-item">
            <FaUsers className="stat-icon" />
            <span>Total Employees: {mappedEmployees.length}</span>
          </div>
          <div className="stat-item">
            <span className="active-indicator">
              Active: {mappedEmployees.filter(emp => emp.status === 'Active').length}
            </span>
          </div>
        </div>
      </div>

      <div className="search-filters">
        <input
          type="text"
          placeholder="Search employees by name..."
          value={searchTerm}
          onChange={e => { setSearchTerm(e.target.value); setCurrentPage(1); }}
          className="search-input"
        />
        <select
          value={entriesPerPage}
          onChange={e => { setEntriesPerPage(Number(e.target.value)); setCurrentPage(1); }}
          className="entries-filter"
        >
          <option value={10}>10 entries</option>
          <option value={25}>25 entries</option>
          <option value={50}>50 entries</option>
          <option value={100}>100 entries</option>
        </select>
      </div>

      <div style={{ overflowX: 'auto', width: '100%' }}>
        <table className="employees-table" style={{ width: '100%', minWidth: '800px', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th onClick={() => requestSort('empId')} style={{ cursor: 'pointer' }}>EmpID {getSortIcon('empId')}</th>
              <th>Status</th>
              <th onClick={() => requestSort('name')} style={{ cursor: 'pointer' }}>Name {getSortIcon('name')}</th>
              <th>Report To</th>
              <th>Division</th>
              <th>Department</th>
              <th>Designation</th>
              <th>Grade</th>
              <th>Zone</th>
              <th>Branch</th>
              <th>Mobile</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {currentEmployees.length > 0 ? currentEmployees.map((emp, idx) => (
              <tr
                key={emp.empId || idx}
                onClick={() => handleSearchClick(emp)}
                style={{ cursor: 'pointer', transition: 'background 0.2s ease' }}
                onMouseEnter={e => e.currentTarget.style.background = '#f0f8ff'}
                onMouseLeave={e => e.currentTarget.style.background = 'transparent'}
              >
                <td>{emp.empId || 'N/A'}</td>
                <td><span className={`status-badge ${emp.status?.toLowerCase() || 'unknown'}`}>{emp.status || 'Unknown'}</span></td>
                <td>{emp.name || 'N/A'}</td>
                <td>{emp.reportTo || 'N/A'}</td>
                <td>{emp.division || 'N/A'}</td>
                <td>{emp.department || 'N/A'}</td>
                <td>{emp.designation || 'N/A'}</td>
                <td>{emp.grade || 'N/A'}</td>
                <td>{emp.zone || 'N/A'}</td>
                <td>{emp.branch || 'N/A'}</td>
                <td>{emp.mobile || 'N/A'}</td>
                <td>{emp.email || 'N/A'}</td>
              </tr>
            )) : (
              <tr>
                <td colSpan="12" style={{ textAlign: 'center', padding: '20px' }}>
                  {mappedEmployees.length === 0 ? 'No employees found' : 'No employees match your search'}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {totalPages > 1 && ( 
        <div className="pagination-controls"> 
        <button onClick={() => handlePageChange(currentPage - 1)} 
        disabled={currentPage === 1} 
        className="pagination-btn" > Previous 
        </button> 
        <span className="pagination-info">Page {currentPage} of {totalPages}</span>
        <button onClick={() => handlePageChange(currentPage + 1)} 
        disabled={currentPage === totalPages} 
        className="pagination-btn" > Next </button> 
        </div> )} 
        </div> 
      ); 
      }; 
      export default EmployeeList;
